@app.route("/getPixel", methods=['GET', 'POST', 'OPTIONS'])
def getPixel():
    global lcd
    d = json.loads(request.get_data())
    p = lcd.getPixel(d['x'],d['y'])
    return json.dumps(p) 


@app.route("/setPixel", methods=['GET', 'POST', 'OPTIONS'])
def setPixel():
    global lcd
    d = json.loads(request.get_data())
    lcd.Pset(d['x'],d['y'],d['c'])
    return ""

@app.route("/getDisplay", methods=['GET', 'POST', 'OPTIONS'])
def getDisplay():
    global lcd
    a=[]
    for x in range(128):
        a.append([])
        for y in range(64):
            a[x].append(lcd.getPixel(x,y))
    return json.dumps(a)

